import { React, useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import { Button, List } from '@material-ui/core';
import { Add, Remove, Adb } from '@material-ui/icons';
import { HamburgerElastic } from 'react-animated-burgers'
import Sidebar from 'react-sidebar';

function changeSizeByBtn() {
    document.getElementById('text').style.fontSize = 95 +'px';
}
function decreaseSizeByBtn() {
    document.getElementById('text').style.fontSize = 50 +'px';
}
function stopVideo(){
    document.getElementById('vid').remove();  
}

function Navbar() {

    const [isOpen, setOpen] = useState(false);
    const handleClick = () => {
        setOpen(!isOpen);
    };
   
    return (
        <div>
            <nav className='navbar'>
                <div style={{ justifySelf: 'flex-start', alignSelf: 'center' }}> 
                    <Button 
                        variant="popover" 
                        onClick={handleClick}
                    >
                        <HamburgerElastic isActive={isOpen} barColor="white"/>
                    </Button>
                </div>
                <div className='nav-container'>
                    <li className='nav-item'>
                        <Link to='/sign-up' className='nav-link'>
                            Sign Up
                        </Link>
                    </li>
                    <Link to='/' className='navbar-logo'>
                        TwoLlama Travels
                    </Link>
                    <li className='nav-item'>
                        <Link to='/log-in' className='nav-link'>
                            Log In
                        </Link>
                    </li>
                </div>
            </nav>
            <Sidebar 
                sidebar={  
                    <List>
                        <div style={{ textAlign: 'center' }}>
                            <b>Settings</b>
                            <hr></hr>
                        </div>
                        <div style={{ marginTop: '50', textAlign: 'center' }}>
                            <b>Text Size</b>
                        </div>
                        <hr></hr>
                        <div style={{ display: 'flex' }}>
                            <Button><Add /></Button>
                            <Button><Remove /></Button>
                        </div>
                        <hr></hr>
                        <div style={{ marginTop: '15px', textAlign: 'center' }}><b>Website</b></div>
                        <hr></hr>
                        <Button style={{ width: '100%' }} endIcon={<Adb />}>Optimize</Button>
                        <hr></hr>
                    </List>
                }
                open={isOpen}
                styles={{ root: { top: "80px" }, sidebar: { background: "rgba(255,255,255,0.7)", bottom: "11%" }, overlay: { top: '80px' }, content: { top: '10px' } }}
            >
            </Sidebar>
        </div>
    )
}

export default Navbar;
