import { React, useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import { Button, List } from '@material-ui/core';
import { Add, Remove, Videocam, VideocamOff } from '@material-ui/icons';
import { HamburgerArrow } from 'react-animated-burgers'
import Sidebar from 'react-sidebar';

function toggleFontSize(toIncrease) {

    if (toIncrease) {
        setSizeCounter(sizeCounter + 1);
    } else {
        setSizeCounter(sizeCounter - 1);
    }

    document.styleSheets[0].deleteRule(0);
    document.styleSheets[0].deleteRule(1);
    document.styleSheets[0].deleteRule(2);
    document.styleSheets[0].deleteRule(3);

    if (sizeCounter === -1) {
        document.styleSheets[0].insertRule("h1 { font-size: xx-large }", 0);
        document.styleSheets[0].insertRule("h2 { font-size: x-large }", 1);
        document.styleSheets[0].insertRule("h3 { font-size: medium }", 2);
        document.styleSheets[0].insertRule("p { font-size: x-small }", 3);
    } 
    else if (sizeCounter == 0) {
        document.styleSheets[0].insertRule("h1 { font-size: xxx-large }", 0);
        document.styleSheets[0].insertRule("h2 { font-size: xx-large }", 1);
        document.styleSheets[0].insertRule("h3 { font-size: large }", 2);
        document.styleSheets[0].insertRule("p { font-size: small }", 3);
    }
    else if (sizeCounter == 1) {
        document.styleSheets[0].insertRule("h1 { font-size: xxxx-large }", 0);
        document.styleSheets[0].insertRule("h2 { font-size: xxx-large }", 1);
        document.styleSheets[0].insertRule("h3 { font-size: x-large }", 2);
        document.styleSheets[0].insertRule("p { font-size: medium }", 3);
    }
}

function Navbar() {

    const [isOpen, setOpen] = useState(false);
    const [isVideoOn, setVideoOn] = useState(true);
    const [sizeCounter, setSizeCounter] = useState(0);

    const handleClick = () => {
        setOpen(!isOpen);
    };

    const toggleVideo = () => {
        if (!isVideoOn) {
            document.getElementById('vid').style.display = "block";
            setVideoOn(true);
        } else {
            document.getElementById('vid').style.display = "none";
            setVideoOn(false);
        }
    }

    return (
        <div>
            <nav className='navbar'>
                <div style={{ justifySelf: 'flex-start', alignSelf: 'center' }}> 
                    <HamburgerArrow isActive={isOpen} onClick={handleClick} barColor="white"/>
                </div>
                <div className='nav-container'>
                    <li className='nav-item'>
                        <Link to='/sign-up' className='nav-link'>
                            <h3>Sign Up</h3>
                        </Link>
                    </li>
                    <Link to='/' className='navbar-logo'>
                        <h2>TwoLlama Travels</h2>
                    </Link>
                    <li className='nav-item'>
                        <Link to='/log-in' className='nav-link'>
                            <h3>Log In</h3>
                        </Link>
                    </li>
                </div>
            </nav>
            <Sidebar 
                sidebar={  
                    <List>
                        <p style={{ textAlign: 'center', color: 'white'}}>Text Size</p>
                        <div style={{ display: 'flex' }}>
                            <Button style={{margin: '5px'}} variant="contained" onClick={toggleFontSize(true)} ><Add /></Button>
                            <Button style={{margin: '5px'}} variant="contained" onClick={toggleFontSize(false)}><Remove /></Button>
                        </div>
                        <div style={{ marginTop: '15px', textAlign: 'center', color: 'white' }}>Optimize</div>
                        {!isVideoOn && <Button style={{ margin: '5px', width: '90%' }} variant="contained" onClick={toggleVideo}><Videocam /></Button>}
                        {isVideoOn && <Button style={{ margin: '5px', width: '90%' }} variant="contained" onClick={toggleVideo}><VideocamOff /></Button>}
                    </List>
                }
                open={isOpen}
                styles={{ root: { top: "80px", right: "90%", bottom: "67%" }, sidebar: { background: "rgba(0,0,0,0.5)" }, content: { top: '10px' } }}
            >
                <div id="blanksoidontgetanerror"></div>
            </Sidebar>
        </div>
    )
}

export default Navbar;
