import { React, useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import { Button, List } from '@material-ui/core';
import { Add, Remove, Videocam, Videocamoff } from '@material-ui/icons';
import { HamburgerElastic } from 'react-animated-burgers'
import Sidebar from 'react-sidebar';

function changeSizeByBtn() {
    document.getElementById('text').style.fontSize = 95 +'px';
}
function decreaseSizeByBtn() {
    document.getElementById('text').style.fontSize = 50 +'px';
}
function stopVideo(){
    if (document.getElementById('vid').style.display === "none"){
        document.getElementById('vid').style.display = "block";
    }else{
        document.getElementById('vid').style.display = "none";
    }
}

function toggleIcon() {
    if (document.getElementById('vid').style.display === "none"){
        return <Videocam />;
    }else{
        return <Videocamoff />;
    }
}

function Navbar() {

    const [isOpen, setOpen] = useState(false);
    
    const handleClick = () => {
        setOpen(!isOpen);
    };

    return (
        <div>
            <nav className='navbar'>
                <div style={{ justifySelf: 'flex-start', alignSelf: 'center' }}> 
                    <Button 
                        variant="popover" 
                        onClick={handleClick}
                    >
                        <HamburgerElastic isActive={isOpen}  barColor="white"/>
                    </Button>
                </div>
                <div className='nav-container'>
                    <li className='nav-item'>
                        <Link to='/sign-up' className='nav-link'>
                            Sign Up
                        </Link>
                    </li>
                    <Link to='/' className='navbar-logo'>
                        TwoLlama Travels
                    </Link>
                    <li className='nav-item'>
                        <Link to='/log-in' className='nav-link'>
                            Log In
                        </Link>
                    </li>
                </div>
            </nav>
            <Sidebar 
                sidebar={  
                    <List>
                        <p style={{ textAlign: 'center', color: 'white'}}>Text Size</p>
                        <div style={{ display: 'flex' }}>
                            <Button style={{margin: '5px'}} variant="contained" onClick={changeSizeByBtn} ><Add /></Button>
                            <Button style={{margin: '5px'}} variant="contained" onClick={decreaseSizeByBtn}><Remove /></Button>
                        </div>
                        <div style={{ marginTop: '15px', textAlign: 'center', color: 'white' }}>Website</div>
                        <Button style={{ margin: '5px', width: '90%' }} variant="contained" endIcon={toggleIcon} onClick={stopVideo}>Optimize</Button>
                    </List>
                }
                open={isOpen}
                styles={{ root: { top: "80px" }, sidebar: { background: "rgba(0,0,0,0.28)", bottom: "75%" }, overlay: { top: '80px' }, content: { top: '10px' } }}
            >
            </Sidebar>
        </div>
    )
}

export default Navbar;
