import { React, useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import { Button, Menu, MenuItem } from '@material-ui/core';
import { HamburgerElastic } from 'react-animated-burgers'

function changeSizeByBtn() {
    document.getElementById('text').style.fontSize = 95 +'px';
}
function decreaseSizeByBtn() {
    document.getElementById('text').style.fontSize = 50 +'px';
}
function stopVideo(){
    document.getElementById('vid').remove();  
}

function Navbar() {

    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
        setAnchorEl(null);
    };
   
    return (
            <nav className='navbar'>
                <div style={{ justifySelf: 'flex-start', alignSelf: 'center' }}> 
                        <Button 
                            id="basic-button"
                            aria-controls="basic-menu"
                            aria-haspopup="true"
                            aria-expanded={open ? 'true' : undefined}
                            variant="popover" 
                            onClick={handleClick}
                        >
                            <HamburgerElastic isActive={open} barColor="white"/>
                        </Button>
                        <Menu 
                            anchorEl={anchorEl}
                            open={open}
                            onClose={handleClose}
                            MenuListProps={{
                                'aria-labelledby': 'basic-button',
                            }}
                            anchorOrigin={{
                                vertical: 'bottom',
                                horizontal: 'left',
                              }}
                            style={{ justifySelf: 'flex-end' }}
                        >
                            Font Size
                            <MenuItem onClick={changeSizeByBtn}>+</MenuItem>
                            <MenuItem onClick={decreaseSizeByBtn}>-</MenuItem>
                            <MenuItem onClick={stopVideo}>No Background</MenuItem>
                            <MenuItem onClick={handleClose}>Close</MenuItem>
                        </Menu>
                    </div>
                <div className='nav-container'>
                    <li className='nav-item'>
                        <Link to='/sign-up' className='nav-link'>
                            Sign Up
                        </Link>
                    </li>
                    <Link to='/' className='navbar-logo'>
                        TwoLlama Travels
                    </Link>
                    <li className='nav-item'>
                        <Link to='/log-in' className='nav-link'>
                            Log In
                        </Link>
                    </li>
                </div>
            </nav>
    )
}

export default Navbar;
