import React from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import {
    MDBNavbar,
    MDBNavbarBrand,
    MDBNavbarNav,
    MDBNavItem,
    MDBNavLink,
    MDBNavbarToggler,
    MDBCollapse,
    MDBContainer
} from 'mdbreact';

function changeSizeByBtn() {
    document.getElementById('text').style.fontSize = 95 +'px';
}
function decreaseSizeByBtn() {
    document.getElementById('text').style.fontSize = 50 +'px';
}
function stopVideo(){
    document.getElementById('vid').remove();  
}

function Navbar() {

    state = {
        collapseID: ''
    };

    toggleCollapse = collapseID => () => {
        this.setState(prevState => ({
          collapseID: prevState.collapseID !== collapseID ? collapseID : ''
        }));
      };

    return (
            <nav className='navbar'>
                <div className='nav-container'>
                    <li className="nav-item">
                        <MDBContainer>
                            <MDBNavbar
                                color='light-blue lighten-4'
                                style={{ marginTop: '20px' }}
                                light
                            >
                                <MDBContainer>
                                    <MDBNavbarBrand>Navbar</MDBNavbarBrand>
                                    <MDBNavbarToggler
                                        onClick={this.toggleCollapse('navbarCollapse1')}
                                    />
                                    <MDBCollapse
                                        id='navbarCollapse1'
                                        isOpen={this.state.collapseID}
                                        navbar
                                    >
                                        <MDBNavbarNav left>
                                            <MDBNavItem active>
                                                <h1>Font Size</h1>
                                                <Button color="success" onClick={changeSizeByBtn}>+</Button>
                                                <Button color="error" onClick={decreaseSizeByBtn}>-</Button>
                                            </MDBNavItem>
                                            <MDBNavItem>
                                                <Button onClick={stopVideo}>No Background</Button>
                                            </MDBNavItem>
                                        </MDBNavbarNav>
                                    </MDBCollapse>
                                </MDBContainer>
                            </MDBNavbar>
                        </MDBContainer>
                    </li>
                    <li className='nav-item'>
                        <Link to='/sign-up' className='nav-link'>
                            Sign Up
                        </Link>
                    </li>
                    <Link to='/' className='navbar-logo'>
                        TwoLlama Travels
                    </Link>
                    <li className='nav-item'>
                        <Link to='/log-in' className='nav-link'>
                            Log In
                        </Link>
                    </li>
                </div>
            </nav>
    )
}

export default Navbar;
