import { React, useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import { Button, List } from '@material-ui/core';
import { Add, Remove, Videocam, VideocamOff } from '@material-ui/icons';
import { HamburgerArrow } from 'react-animated-burgers'
import Sidebar from 'react-sidebar';

function increaseFontSize() {
    let h1Elements = document.getElementsByTagName('h1');
    while (h1Elements.length)
        h1Elements[0].style.fontSize += "10px";
    // document.getElementsByTagName('h2').style.fontSize += "10px";
    // document.getElementsByTagName('h3').style.fontSize += "10px";
    // document.getElementsByTagName('p').style.fontSize += "10px";
                
}
function decreaseFontSize() {
    // document.getElementsByTagName('h1').style.fontSize -= "10px";
    // document.getElementsByTagName('h2').style.fontSize -= "10px";
    // document.getElementsByTagName('h3').style.fontSize -= "10px";
    // document.getElementsByTagName('p').style.fontSize -= "10px";
}

function Navbar() {

    const [isOpen, setOpen] = useState(false);
    const [isVideoOn, setVideoOn] = useState(true);
    
    const handleClick = () => {
        setOpen(!isOpen);
    };

    const toggleVideo = () => {
        if (!isVideoOn) {
            document.getElementById('vid').style.display = "block";
            setVideoOn(true);
        } else {
            document.getElementById('vid').style.display = "none";
            setVideoOn(false);
        }
    }

    return (
        <div>
            <nav className='navbar'>
                <div style={{ justifySelf: 'flex-start', alignSelf: 'center' }}> 
                    <HamburgerArrow isActive={isOpen} onClick={handleClick} barColor="white"/>
                </div>
                <div className='nav-container'>
                    <li className='nav-item'>
                        <Link to='/sign-up' className='nav-link'>
                            <h3>Sign Up</h3>
                        </Link>
                    </li>
                    <Link to='/' className='navbar-logo'>
                        <h2>TwoLlama Travels</h2>
                    </Link>
                    <li className='nav-item'>
                        <Link to='/log-in' className='nav-link'>
                            <h3>Log In</h3>
                        </Link>
                    </li>
                </div>
            </nav>
            <Sidebar 
                sidebar={  
                    <List>
                        <p style={{ textAlign: 'center', color: 'white'}}>Text Size</p>
                        <div style={{ display: 'flex' }}>
                            <Button style={{margin: '5px'}} variant="contained" onClick={increaseFontSize} ><Add /></Button>
                            <Button style={{margin: '5px'}} variant="contained" onClick={decreaseFontSize}><Remove /></Button>
                        </div>
                        <div style={{ marginTop: '15px', textAlign: 'center', color: 'white' }}>Optimize</div>
                        {!isVideoOn && <Button style={{ margin: '5px', width: '90%' }} variant="contained" onClick={toggleVideo}><Videocam /></Button>}
                        {isVideoOn && <Button style={{ margin: '5px', width: '90%' }} variant="contained" onClick={toggleVideo}><VideocamOff /></Button>}
                    </List>
                }
                open={isOpen}
                styles={{ root: { top: "80px", right: "90%", bottom: "67%" }, sidebar: { background: "rgba(0,0,0,0.5)" }, content: { top: '10px' } }}
            >
                <div id="blanksoidontgetanerror"></div>
            </Sidebar>
        </div>
    )
}

export default Navbar;
