import React from 'react'
import '../../App.css'
import WeatherWidget from '../WeatherWidget'
import video from '../../images/llamawalking.mp4'
import FlightWidget from '../FlightWidget';
import Hotels from '../Hotels';
import Itinerary from '../Itinerary';
import { Button } from "@material-ui/core";


class WidgetPage extends React.Component{
  constructor(props){
    super(props);
    this.state = {value: '', value2: ''};
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange (destination, location){
    this.setState({value: destination, value2: location});
  }
  
  stopVideo(){
    document.getElementById('vid').remove();
  }

  render(){
    return (
      <div className="widget-page">
        <br></br>
        <video id='vid' src={video} autoPlay loop muted/>
        <form>
          <label>
            <input type="text" placeholder='Enter Your Destination...'/>
          </label>
          <label>
            <input type="text"  placeholder='Enter Your Location...'/>
          </label>
          <Button type="submit" onClick={this.handleChange}>Submit</Button>
        </form>
          {this.state.value && <WeatherWidget destination={this.state.value}/>}
          {this.state.value && <FlightWidget destination={this.state.value} place={this.state.value2}/>}
          {this.state.value && <Hotels destination={this.state.value} />}
          {this.state.value && <Itinerary destination={this.state.value} />}
  
          <button type="button" onClick={this.stopVideo}>
            No Background
          </button>
            
      </div>
    );
  }
}

export default WidgetPage;
