import { React, useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import { Button, List } from '@material-ui/core';
import { Add, Remove, Videocam, VideocamOff } from '@material-ui/icons';
import { HamburgerArrow } from 'react-animated-burgers'
import Sidebar from 'react-sidebar';

function Navbar() {

    const [isOpen, setOpen] = useState(false);
    const [isVideoOn, setVideoOn] = useState(true);
    let sizeCounter = 0;

    const handleClick = () => {
        setOpen(!isOpen);
    };

    const toggleVideo = () => {
        if (!isVideoOn) {
            document.getElementById('vid').style.display = "block";
            setVideoOn(true);
        } else {
            document.getElementById('vid').style.display = "none";
            setVideoOn(false);
        }
    }

    const increaseFontSize = () => {
        if (sizeCounter < 1) {
            sizeCounter++;
            console.log("Increasing font size");
            toggleFontSize();
        }
    }

    const decreaseFontSize = () => {
        if (sizeCounter > -1) {
            sizeCounter--;
            console.log('Decreasing font size');
            toggleFontSize();
        }
    }

    const toggleFontSize = () => {

        let currentSheet = document.styleSheets[0];
        while (currentSheet.parentStyleSheet) {
            currentSheet = currentSheet.parentStyleSheet;
        }
        console.log(currentSheet);
        console.log(sizeCounter);
    
        if (sizeCounter === -1) {
            currentSheet.insertRule("h1 { font-size: xx-large }", currentSheet.cssRules.length);
            currentSheet.insertRule("h2 { font-size: x-large }", currentSheet.cssRules.length);
            currentSheet.insertRule("h3 { font-size: medium }", currentSheet.cssRules.length);
            currentSheet.insertRule("p { font-size: x-small }", currentSheet.cssRules.length);
        } 
        else if (sizeCounter === 0) {
            currentSheet.insertRule("h1 { font-size: xxx-large }", currentSheet.cssRules.length);
            currentSheet.insertRule("h2 { font-size: xx-large }", currentSheet.cssRules.length);
            currentSheet.insertRule("h3 { font-size: large }", currentSheet.cssRules.length);
            currentSheet.insertRule("p { font-size: small }", currentSheet.cssRules.length);
        }
        else if (sizeCounter === 1) {
            currentSheet.insertRule("h1 { font-size: xxxx-large }", currentSheet.cssRules.length);
            currentSheet.insertRule("h2 { font-size: xxx-large }", currentSheet.cssRules.length);
            currentSheet.insertRule("h3 { font-size: x-large }", currentSheet.cssRules.length);
            currentSheet.insertRule("p { font-size: medium }", currentSheet.cssRules.length);
        }
    }

    return (
        <div>
            <nav className='navbar'>
                <div style={{ justifySelf: 'flex-start', alignSelf: 'center' }}> 
                    <HamburgerArrow isActive={isOpen} onClick={handleClick} barColor="white"/>
                </div>
                <div className='nav-container'>
                    <li className='nav-item'>
                        <Link to='/sign-up' className='nav-link'>
                            <h3>Sign Up</h3>
                        </Link>
                    </li>
                    <Link to='/' className='navbar-logo'>
                        <h2>TwoLlama Travels</h2>
                    </Link>
                    <li className='nav-item'>
                        <Link to='/log-in' className='nav-link'>
                            <h3>Log In</h3>
                        </Link>
                    </li>
                </div>
            </nav>
            <Sidebar 
                sidebar={  
                    <List>
                        <p style={{ textAlign: 'center', color: 'white'}}>Text Size</p>
                        <div style={{ display: 'flex' }}>
                            <Button style={{margin: '5px'}} variant="contained" onClick={increaseFontSize} disabled={sizeCounter === 1} ><Add /></Button>
                            <Button style={{margin: '5px'}} variant="contained" onClick={decreaseFontSize} disabled={sizeCounter === -1}><Remove /></Button>
                        </div>
                        <p style={{ marginTop: '15px', textAlign: 'center', color: 'white' }}>Optimize</p>
                        {!isVideoOn && <Button style={{ margin: '5px', width: '90%' }} variant="contained" onClick={toggleVideo} ><Videocam /></Button>}
                        {isVideoOn && <Button style={{ margin: '5px', width: '90%' }} variant="contained" onClick={toggleVideo}><VideocamOff /></Button>}
                    </List>
                }
                open={isOpen}
                styles={{ root: { top: "80px", right: "90%", bottom: "67%" }, sidebar: { background: "rgba(0,0,0,0.5)" }, content: { top: '10px' } }}
            >
                <div id="blanksoidontgetanerror"></div>
            </Sidebar>
        </div>
    )
}

export default Navbar;
